        const isMobile = window.innerWidth < 768;
        const wrapper = document.createElement("div");
        wrapper.classList.add("textWrapper");
        wrapper.style.display = "grid";
        wrapper.style.gridColumn = isMobile ? txt.mobileCol : txt.desktopCol;
        wrapper.style.gridRow = isMobile ? txt.mobileRow : txt.desktopRow;
        wrapper.style.position = "relative";
        wrapper.style.height = `calc((var(--maxWidth) / var(--baseValue)) * ${isMobile ? txt.mobileEndRow : txt.desktopEndRow})`;
        wrapper.style.overflow = "visible";
        wrapper.style.pointerEvents = "none";
        const stickyText = document.createElement("div");
        stickyText.classList.add("stickyContainer", txt.fontSize);
        stickyText.innerHTML = txt.content;
        stickyText.style.position = "sticky";
        stickyText.style.alignSelf = "start";
        wrapper.appendChild(stickyText);
        container.appendChild(wrapper);
